for(let i=0;i<img.length;i++){
    let thumbnail = document.createElement('IMG');
    thumbnail.setAttribute('src',img[i].link);
    thumbnail.setAttribute('oncontextmenu','return false;');
    thumbnail.classList.add('image');
    let btn = '';
    thumbnail.addEventListener('click', (e)=>{
        orgName.innerHTML=img[i].name;
        Ceo.innerHTML=img[i].ceo;
        Year.innerHTML=img[i].Year;
        mouseCords.innerHTML = e.clientX +' , '+ e.clientY;
        thumbnail.addEventListener('contextmenu',(e)=>{
            e.preventDefault();
            btn = 'Right';
        });
        if(e.button == 0){
            btn = 'Left';
        }         
        mouseClicked.innerHTML = btn;
    });
    images.appendChild(thumbnail);   
}


